def loadbar(iteration, total, prefix, suffix, decimals, length, fill = ('', '', 1, 100, '>')):
    percent = ('{0:.' + str(decimals) + 'f}').format(100 * (iteration / float(total)))
    filledLength = int(length * iteration // total)
    bar = fill * filledLength + '-' * (length - filledLength)
    print(f'''\r{prefix} |{bar}| {percent}% {suffix}''', '\r', **('end',))
    system('title ' + f'''BitCry {v} - Loading: {percent}%''')
    if iteration == total:
        print()
        return None


